<?phpnamespace TelegramBot\Api\Handlers\Abstracts;use function call_user_func_array;use function method_exists;abstract class BaseAbstract{    public function populateProperties()    {    }    public function beforeCallMethod($method, array $params = [])    {        return true;    }    public function afterCallMethod($method, $params, $result = null)    {        return true;    }    public function callMethod($method, $params = [])    {        if (method_exists($this, $method)) {            if (!$this->beforeCallMethod($method, $params)) {                return false;            }            $result = call_user_func_array([$this, $method], $params);            $this->afterCallMethod($method, $params, $result);            return $result;        }        return false;    }}